mapper.select:

SELECT u.*,c.dept_name,IFNULL(c2.dept_id,0) dept_fdy from user u 
  left join dept c on c.dept_id=u.dept_id 
  left join dept c2 on c2.user_id=u.user_id 




	u.user_id = #{user_id}




	and u.user_name = #{user_name}




	and u.user_pass = #{user_pass}




	and u.user_type = #{user_type}


SELECT * FROM (
SELECT u.*,c.dept_name,IFNULL(c2.dept_id,0) dept_fdy from user u 
  left join dept c on c.dept_id=u.dept_id 
  left join dept c2 on c2.user_id=u.user_id 




	u.user_id = #{user_id}




	and u.user_name like '%${user_name}%'




	and u.real_name like '%${real_name}%'




	and u.dept_id = #{dept_id}




	and u.user_type = #{user_type}





order by rand()) t




order by u.user_id asc) t




Limit ${start},${limit}


